/* CSS Custom Properties */
/* Main CSS for Homepage */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background-color: #1d3557;
  font-family: "Montserrat", sans-serif;
  font-size: 1.1rem;
  color: #f1faee;
  transition: all 0.4s ease-in-out;
}

header {
  height: 100vh;
  color: white;
  overflow: hidden;
}

section {
  min-height: 100vh;
  width: 100%;
  position: absolute;
  left: 0;
  top: 0;
  padding: 3rem 10rem;
  background-color: #1d3557 !important;
  text-align: center;
  display: grid;
  grid-gap: 1em;
  grid-template-columns: repeat(3, 1fr);
}
section .section-title {
  margin: 0 auto;
  display: flex;
  justify-content: center;
  text-align: center;
  grid-column-start: span 3;
  font-size: 2em;
}
section .section-title span {
  color: #e63946;
}
section .section-title .section-title-background {
  position: absolute;
  z-index: -1;
  font-size: 3.5em;
  font-weight: 900;
  color: #dbe1e8;
  opacity: 0.05;
  margin-top: -0.15em;
  line-height: 80%;
}

/* Controls */
.controls {
  position: fixed;
  top: 30%;
  right: 3%;
  z-index: 10;
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  align-items: center;
  transform: translateY(-50%);
  width: 70px;
  height: 70px;
}
.controls .active-btn {
  background-color: black !important;
  transition: all 0.4s ease-in-out;
  border: 1px solid white;
}
.controls i {
  color: white;
}

.control {
  padding: 1rem;
  margin: 0.7rem;
  cursor: pointer;
  background-color: #b2becd;
  width: 55px;
  height: 55px;
  border-radius: 50%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: 3px 3px 15px 0px black;
}
.control i {
  display: flex;
  justify-content: center;
  align-items: center;
}

.control-icon {
  font-size: 1.2rem;
  pointer-events: none;
  z-index: 5;
  width: 1.3rem;
  height: 1.3rem;
  padding: 0.5rem;
  margin: 0.2rem;
  color: black;
}

/* Tooltip */
.control:hover .tooltip {
  opacity: 1;
  margin-right: 120px;
  transform: rotate(0deg) scale(1);
  padding: 0.2em 2em 0.2em 0.4em;
  display: block;
}
.control:hover .tooltip-home, .control:hover .tooltip-about {
  margin-right: 90px;
}
.control:hover .tooltip-services {
  margin-right: 105px;
}
.control:hover .tooltip-contact {
  margin-right: 103px;
}
.control .tooltip {
  opacity: 0;
  display: none;
  position: absolute;
  margin-right: 4em;
  display: flex;
  justify-content: right;
  transition: all 0.4s ease-in-out;
  background-color: black;
  padding: 0.2em 2em 0.2em 0.4em;
  z-index: -1;
  border-radius: 50px;
  transform: rotate(-90deg) scale(0.2);
  transform-origin: right;
  pointer-events: none;
}

/* Sections  */
.section-info .divider {
  content: "";
  margin: 5em auto;
  display: flex;
  justify-content: center;
  width: 70%;
  height: 3px;
  background-color: #12181b;
  max-width: 30em;
  opacity: 0.3;
}

