// css reset

/* Box sizing rules */
*,
*::before,
*::after {
  box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
  margin: 0;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role="list"],
ol[role="list"] {
  list-style: none;
}

/* Set core root defaults */
html:focus-within {
  scroll-behavior: smooth;
}

/* Set core body defaults */
body {
  min-height: 100vh;
  text-rendering: optimizeSpeed;
  line-height: 1.5;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
  text-decoration-skip-ink: auto;
}

/* Make images easier to work with */
img,
picture {
  max-width: 100%;
  display: block;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
  font: inherit;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
  html:focus-within {
    scroll-behavior: auto;
  }

  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

/* CSS Custom Properties */
$highlight-color: #e63946;
$light-main-text-color: #1d3557;
$dark-main-text-color: #f1faee;
$color-grey0: #f8f8f8;
$color-grey-1: #dbe1e8;
$color-grey-2: #b2becd;
$color-grey-3: #6c7983;
$color-grey-4: #454e56;
$color-grey-5: #2a2e35;
$color-grey-6: #12181b;
$light-bg-color: #f1faee;
$dark-bg-color: #1d3557;
$secondary-bg-color: black;
$header-font: "Roboto Condensed", sans-serif, arial;
$paragraph-font: "Montserrat", sans-serif;
$transition-default: all 0.4s ease-in-out;
$box-shadow: 3px 3px 15px 0px black;
$box-shadow-on-hover: 5px 5px 15px 0px black;

// Media query breakpoints

// Small Devices (576px)
$mq-small: 36em;
// Med Devices (768px)
$mq-med: 48em;
// Lg Devices (992px)
$mq-lg: 62em;
// XL Devices (1200px)
$mq-xl: 75em;
// XXL Devices (1400px)
$mq-xxl: 87.5em;

// Custom classes
.highlight {
  color: $highlight-color;
}

/* Main CSS for Homepage */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background-color: $dark-bg-color;
  font-family: $paragraph-font;
  font-size: 1.1rem;
  color: $dark-main-text-color;
  transition: $transition-default;
  overflow-x: hidden;
}

header {
  height: 100vh;
  color: white;
  overflow: hidden;
}

section {
  min-height: 100vh;
  width: 100dvw;
  // position: absolute;
  // left: 0;
  // top: 0;
  // padding: 3rem;
  background-color: $dark-bg-color !important;
  text-align: center;
  // display: grid;
  // grid-gap: 1em;
  // grid-template-columns: repeat(3, 1fr);
  .section-title {
    margin: 0 auto;
    display: flex;
    justify-content: center;
    text-align: center;
    grid-column-start: span 3;
    font-size: 2em;
    span {
      color: $highlight-color;
    }
    .section-title-background {
      position: absolute;
      z-index: -1;
      font-size: 3.5em;
      font-weight: 900;
      color: $color-grey-1;
      opacity: 0.05;
      margin-top: -0.15em;
      line-height: 80%;
    }
  }
}

/* Controls */
.controls {
  position: fixed;
  top: 30%;
  right: 3%;
  z-index: 10;
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  align-items: center;
  transform: translateY(-50%);
  width: 70px;
  height: 70px;
  .active-btn {
    background-color: $secondary-bg-color !important;
    transition: $transition-default;
    border: 1px solid white;
  }
  i {
    color: white;
  }
}

.control {
  padding: 1rem;
  margin: 0.7rem;
  cursor: pointer;
  background-color: $color-grey-2;
  width: 55px;
  height: 55px;
  border-radius: 50%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: $box-shadow;
  i {
    display: flex;
    justify-content: center;
    align-items: center;
  }
}

.control-icon {
  font-size: 1.2rem;
  pointer-events: none;
  z-index: 5;
  width: 1.3rem;
  height: 1.3rem;
  padding: 0.5rem;
  margin: 0.2rem;
  color: $secondary-bg-color;
}

/* Tooltip */
.control {
  &:hover .tooltip {
    opacity: 1;
    margin-right: 120px;
    transform: rotate(0deg) scale(1);
    padding: 0.2em 2em 0.2em 0.4em;
    display: block;
  }
  &:hover .tooltip-home,
  &:hover .tooltip-about {
    margin-right: 90px;
  }

  &:hover .tooltip-services {
    margin-right: 105px;
  }
  &:hover .tooltip-contact {
    margin-right: 103px;
  }
  .tooltip {
    opacity: 0;
    display: none;
    position: absolute;
    margin-right: 4em;
    display: flex;
    justify-content: right;
    transition: $transition-default;
    background-color: $secondary-bg-color;
    padding: 0.2em 2em 0.2em 0.4em;
    z-index: -1;
    border-radius: 50px;
    transform: rotate(-90deg) scale(0.2);
    transform-origin: right;
    pointer-events: none;
  }
}
/* Sections  */

.section-info .divider {
  content: "";
  margin: 5em auto;
  display: flex;
  justify-content: center;
  width: 70%;
  height: 3px;
  background-color: $color-grey-6;
  max-width: 30em;
  opacity: 0.3;
}

.section {
  transform: translateY(-100%) scale(0);
  transition: $transition-default;
  overflow: hidden;
  padding-bottom: 3em;
  background-color: $light-bg-color;
}

.sec1 {
  display: none;
  transform: translateY(0) scale(1);
}

.sec2 {
  display: none;
  transform: translateY(0) scale(1);
  .section-info {
    max-width: 900px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin: 0 auto;
    margin-top: 5em;
    .about-section-1 {
      display: flex;
      flex-direction: row;
      margin-bottom: 5em;
      margin-top: 5em;
      @media screen and (max-width: 780px) {
        flex-direction: column;
      }
      .triangleDots {
        position: absolute;
        top: 25%;
        right: 0;
        width: 40%;
        z-index: -1;
      }
      .about-sec1-text {
        width: 100%;
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        gap: 2em;
        @media screen and (max-width: 950px) {
          transform: translateY(-10%);
        }
        .about-sec1-text-heading {
          text-align: left;
          h3 {
            padding-left: 1rem;
            padding-right: 10px;
            font-size: calc(12px + 5vmin);
            span {
              color: $highlight-color;
            }
          }
        }
        .about-sec1-text-par {
          text-align: left;
