            flex-shrink: 0;
            width: 100%;
            object-fit: cover;
            z-index: 10;
            box-shadow: 10px 10px 0px 0px $highlight-color;
          }
          .about-section1-mobile-background {
            height: 0rem;
            @media screen and (max-width: 780px) {
              content: "";
              height: 9rem;
              width: 100%;
              z-index: 0;
              box-shadow: 5px 5px 0px 5px $highlight-color;
              margin-top: -3rem;
            }
          }
        }
      }
    }
  }
}

.section-info .divider {
  content: "";
  margin: 5em auto;
  display: flex;
  justify-content: center;
  width: 70%;
  height: 3px;
  background-color: $color-grey-6;
