    $width-box: clamp(15em, 45vw, 50vw);
  }
  $offset-x-left: calc(-10px - 0.390625vw);
  $offset-x-right: calc(10px + 0.390625vw);
  $offset-y-down: calc(10px + 0.390625vw);
  $offset-y-up: calc(-10px - 0.390625vw);
  padding-bottom: 3rem;

  .contact-container {
    display: grid;
    grid-template-columns: 1fr;
    place-items: center;
    margin: 1rem auto;
    @media (min-width: $mq-med) {
      grid-template-columns: 1fr 1fr;
      justify-items: center;
      align-items: start;
    }
    .section-title {
      grid-column: 1 / -1;
    }

    .contact-header {
      grid-column: 1 / -1;
      margin: 1.5rem;
      font-size: 1.2rem;
      max-width: 40rem;
      a {
        text-decoration: none;
      }
    }

    .contact-text {
      margin: 1.5rem auto 4rem;
      background-color: $color-grey-2;
      color: $light-main-text-color;
      box-shadow: $offset-x-left $offset-y-down $highlight-color;
      position: relative;
      display: flex;
      justify-content: center;
      align-items: center;
      max-width: max(390px, $width-box);
      width: $width-box;
      margin: 1em;
      padding: 1em;
      z-index: 5;
      @media (min-width: $mq-med) {
        max-width: 100%;
        padding: 2rem;
      }
      .triangle {
        position: absolute;
        color: white;
        right: 0;
        bottom: 1px;
        translate: 50% 50%;

        border-color: transparent;
        border-bottom-color: $color-grey-2;
        border-width: calc($width-box / 6);
        border-style: solid;

        rotate: 45deg;
        z-index: 1;

        filter: drop-shadow(8px 8px $highlight-color);
      }
      .dots {
        position: absolute;
        background-image: url(../assets/triangleDots.png);
        background-position: center;
        background-repeat: no-repeat;
        background-size: contain;
        z-index: 3;
        right: calc($width-box / 50);
        bottom: calc($width-box / -50);
        width: calc($width-box / 6);
        height: calc($width-box / 6);
        translate: 0 100%;
      }
      .social-p {
        position: absolute;
        bottom: -1rem;
        display: flex;
        justify-content: center;
        align-items: center;
        translate: -10% 100%;
        color: $color-grey0;
        @media (min-width: $mq-small) {
          translate: -20% 100%;
        }
      }
      .icon-container {
        display: flex;
        justify-content: space-between;
        align-items: center;
        position: absolute;
        color: white;
        right: 6%;
        z-index: 1000;
        bottom: -5%;
        rotate: 45deg;
        translate: 25% 125%;
        width: calc($width-box / 4);
        height: calc($width-box / 4);
        i {
          margin-top: 1rem;
          rotate: -45deg;
          cursor: pointer;
          font-size: calc(15px + 0.390625vw);
          transition: $transition-default;
          border-radius: 50%;
          &:hover {
            color: $highlight-color;
            outline: 1px solid $highlight-color;
            outline-offset: 0.5rem;
          }
        }
      }
    }

    #contact-form {
      /* reset */
      form input,
      form select,
      form textarea,
      form fieldset,
      form optgroup,
      form label,
      .StripeElement {
        font-family: inherit;
        font-size: 100%;
        color: inherit;
        border: none;
        border-radius: 0;
        display: block;
        width: 100%;
        padding: 0;
        margin: 0;
        appearance: none;
        -webkit-appearance: none;
        -moz-appearance: none;
      }
      form label,
      form legend {
        font-size: 0.825rem;
        margin-bottom: 0.5rem;
      }
      /* border, padding, margin, width */
      form input,
      form select,
      form textarea,
      .StripeElement {
        box-sizing: border-box;
        border: 1px solid rgba(0, 0, 0, 0.2);
        background-color: rgba(255, 255, 255, 0.9);
        padding: 0.75em 1rem;
        margin-bottom: 1.5rem;
      }
      form input:focus,
      form select:focus,
      form textarea:focus,
      .StripeElement:focus {
        background-color: white;
        outline-style: solid;
        outline-width: thin;
        outline-color: gray;
        outline-offset: -1px;
      }
      form [type="text"],
      form [type="email"],
      .StripeElement {
        width: 100%;
      }
      form [type="button"],
      form [type="submit"],
      form [type="reset"] {
        width: auto;
        cursor: pointer;
        -webkit-appearance: button;
        -moz-appearance: button;
        appearance: button;
      }
      form [type="button"]:focus,
      form [type="submit"]:focus,
      form [type="reset"]:focus {
        outline: none;
      }

      form select {
        text-transform: none;
      }

      position: relative;
      width: min(80%, 500px);
      margin: calc($width-box / 3) 1rem 3rem 1rem;
      @media (min-width: $mq-med) {
        margin: 1rem auto 1rem 1rem;
      }
      background-color: $color-grey-2;
      padding: 1rem;
      &::after {
        position: absolute;
        content: "";
        inset: 0;
        translate: $offset-x-right $offset-y-down;
        width: 100%;
        height: 100%;
        z-index: -1;
        background-color: $highlight-color;
      }
      fieldset {
        legend {
          font-size: 1.4rem;
          font-weight: bold;
          text-transform: uppercase;
          color: $color-grey-4;
        }
      }

      ul {
        list-style: none;
        label {
          font-weight: bold;
          display: block;
          margin-bottom: 0.5rem;
        }
        textarea {
          min-height: 15rem;
        }
        textarea,
        input {
          font-size: 1.2rem;
          font-family: $paragraph-font;
        }

        & li + li {
          margin-top: 1em;
        }
        input:focus,
        textarea:focus {
          border: 2px solid #000;
        }
        .btn {
          font-size: larger;
          button {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin: 1rem auto;
            width: min(80%, 300px);
            padding: 0.5rem 1rem;
            background-color: $dark-bg-color;
            border: 2px solid $highlight-color;
            border-radius: 50px;
            transition: $transition-default;
            &:hover {
              background-color: $highlight-color;
              border: 2px solid #000;
              .cta-text {
                color: #000;
              }
              .arrow {
                transform: translateX(0);
                opacity: 1;
              }
              .cta-icon {
                transform: rotate(1440deg);
                background-color: #000;
                color: white;
              }
            }
            .cta-text {
              padding: 0.3em 0.3em;
              font-weight: bolder;
              color: $highlight-color;
              transition: $transition-default;
              z-index: 10;
            }
            .arrow {
              transition: $transition-default;
              display: inline-block;
              font-size: 2rem;
              transform: translateX(-100%);
              color: black;
              z-index: 0;
              opacity: 0;
            }
            .cta-icon {
              font-size: 1.4rem;
              border: 1px solid $highlight-color;
              border-radius: 50%;
              padding: 0.4em 0.6em;
              background-color: $highlight-color;
              color: $secondary-bg-color;
              transition: $transition-default;
            }
          }
        }
      }
    }
  }
}

/* Submit Btn */
.main-contact-con .contact-right .cta {
  margin-top: 2.8em;
  width: 15em;
  border: 1px solid $highlight-color;
  border-radius: 50px;
  transition: $transition-default;
  cursor: pointer;
  margin-left: 0.5em;
  .cta-btn {
    display: flex;
    justify-content: space-between;
    font-size: larger;
    text-decoration: none;
  }
  .cta-txt {
    padding: 0.3em 0.3em;
    margin-left: 1em;
    font-weight: bolder;
    color: $highlight-color;
    transition: $transition-default;
    display: flex;
    justify-content: center;
    align-items: center;
    .arrow {
      opacity: 0;
      color: $secondary-bg-color;
      margin-left: 0em;
      transition: $transition-default;
    }
  }
  .cta-icon {
    font-size: 1.4rem;
    border: 1px solid $highlight-color;
    border-radius: 50%;
    padding: 0.4em 0.6em;
    background-color: $highlight-color;
    color: $secondary-bg-color;
    transition: $transition-default;
  }
  // hover test
  &:hover {
    background-color: $highlight-color;
    padding-left: 0.2em;
    .cta-txt {
      color: $secondary-bg-color;
    }
    .cta-icon {
      background-color: $secondary-bg-color;
      color: $highlight-color;
      transform: rotate(1080deg);
    }
    .arrow {
      opacity: 1;
      margin-left: 0.8em;
    }
  }
}

