/* Submit Btn */
.main-contact-con .contact-right .cta {
  margin-top: 2.8em;
  width: 15em;
  border: 1px solid $highlight-color;
  border-radius: 50px;
  transition: $transition-default;
  cursor: pointer;
  margin-left: 0.5em;
  .cta-btn {
    display: flex;
    justify-content: space-between;
    font-size: larger;
    text-decoration: none;
  }
  .cta-txt {
    padding: 0.3em 0.3em;
    margin-left: 1em;
    font-weight: bolder;
    color: $highlight-color;
    transition: $transition-default;
    display: flex;
    justify-content: center;
    align-items: center;
    .arrow {
      opacity: 0;
      color: $secondary-bg-color;
      margin-left: 0em;
      transition: $transition-default;
    }
  }
  .cta-icon {
    font-size: 1.4rem;
    border: 1px solid $highlight-color;
    border-radius: 50%;
    padding: 0.4em 0.6em;
    background-color: $highlight-color;
    color: $secondary-bg-color;
    transition: $transition-default;
  }
  // hover test
  &:hover {
    background-color: $highlight-color;
    padding-left: 0.2em;
    .cta-txt {
      color: $secondary-bg-color;
    }
    .cta-icon {
      background-color: $secondary-bg-color;
      color: $highlight-color;
      transform: rotate(1080deg);
    }
    .arrow {
      opacity: 1;
      margin-left: 0.8em;
    }
  }
}

